def counting(handles)
  handles_nombre = handles.count
  puts "Il y'a #{handles_nombre} handles. Utilise cette info comme tu veux."
end

def smallest(handles)
  handles_pluscourt = handles.min_by(&:length) #Méthode pour trouver le plus court dans un array
  puts "L'handle le plus court de la base de données est #{handles_pluscourt}"
end

def five(handles)
  step1 = handles.select { |tri| tri.length == 6} #select parcourt l'array et stock dans la variable |tri| tous les pseudos de 6 caractères
  step2 = step1.map { |secondtri| secondtri[1..]} #map transforme une collection (ici les pseudos que j'ai filtré et stocké dans step1). Là on vient split les pseudos à l'index 1 (donc on enlève le @)
  step3 = step2.count
  puts "Il y'a #{step3} handles qui contiennent 5 caractères, c'est pas beaucoup. "
end

def uppercase(handles)
  x = handles.map {|maj| maj.delete_prefix("@")} #La méthode delete_prefix supprime ici le @ 
  y = x.count do |h| # La boucle count incrémente de 1 (fait augmenter le compteur) à chaque fois que la boucle rencontre la condition...
  h[0].match?(/\p{Lu}/) # Premier caracètre de handles match avec majuscule
  end
  puts "Il y a #{y} handles qui commencent par une majuscule, ça fait beaucoup là... non?"
end

def sort_handles(handles)
  x = handles.sort  #La méthode sort tri par nombre de caractères
  puts "Voici la liste: #{x}"
end


def sort_by_length(handles)
  x = handles.sort_by(&:length) #sort_by(&:length) tri par longueur
  puts "Voici la liste: #{x}"
end

def epenser(handles)
  epenser = handles.index("@epenser") #La méthode index permet de connaître une position dans un array
  puts "La position d'@epenser dans la base de données est #{epenser}, il n'est pas prêt de passer!"
end

def category(handles)
  handles.map(&:length).tally.sort.each do |length, count|
  puts "Handles à #{length} lettre(s) : #{count}"
  end
end

