def perform(handles)
  while true do
  system("clear")
  menu
  choix = gets.chomp.to_i

  case choix
    when 1
      counting(handles)
      puts "\nAppuie sur Entrée pour revenir au menu (si tu veux hein, t'es pas obligé)"
      gets
    when 2
      smallest(handles)
      puts "\nAppuie sur Entrée pour revenir au menu (si tu veux hein, t'es pas obligé)"
      gets
    when 3
      five(handles)
      puts "\nAppuie sur Entrée pour revenir au menu (si tu veux hein, t'es pas obligé)"
      gets
    when 4
      uppercase(handles)
      puts "\nAppuie sur Entrée pour revenir au menu (si tu veux hein, t'es pas obligé)"
      gets
    when 5
      sort_handles(handles)
      puts "\nAppuie sur Entrée pour revenir au menu (si tu veux hein, t'es pas obligé)"
      gets
    when 6
      sort_by_length(handles)
      puts "\nAppuie sur Entrée revenir au menu (si tu veux hein, t'es pas obligé)"
      gets
    when 7
      epenser(handles)
      puts "\nAppuie sur Entrée pour revenir au menu (si tu veux hein, t'es pas obligé)"
      gets
    when 8
      category(handles)
      puts "\nAppuie sur Entrée pour revenir au menu (si tu veux hein, t'es pas obligé)"
      gets  
    when 9
      break #Pour sortir de la boucle
      puts "\nAppuie sur Entrée pour revenir au menu (si tu veux hein, t'es pas obligé)"
      gets
    else
      puts "Saisie inconnue, concentre-toi un peu, le menu est juste là"
    end

  end
end

perform(handles)